import React, {Component} from 'react'
import {Space, Table, Tag} from 'antd';
import {connect} from 'react-redux';
import {fetchUsers} from '../../actions/users';

// const data = [
//     {
//         key: '1',
//         name: 'John Brown',
//         age: 32,
//         address: 'New York No. 1 Lake Park',
//         tags: ['nice', 'developer'],
//     },
//     {
//         key: '2',
//         name: 'Jim Green',
//         age: 42,
//         address: 'London No. 1 Lake Park',
//         tags: ['loser'],
//     },
//     {
//         key: '3',
//         name: 'Joe Black',
//         age: 32,
//         address: 'Sidney No. 1 Lake Park',
//         tags: ['cool', 'teacher'],
//     },
// ];

class ListUser extends Component {

    constructor() {
        super()
        this.state = {
            columns: [
                {
                    title: 'ID',
                    dataIndex: 'id',
                    key: 'id',
                    render: text => <a>{text}</a>,
                },
                {
                    title: 'Name',
                    dataIndex: 'name',
                    key: 'name',
                    render: text => <a>{text}</a>,
                },
                {
                    title: 'Class',
                    dataIndex: 'class',
                    key: 'class',
                    render: text => <a>{text}</a>,
                },
                {
                    title: 'Marks',
                    key: 'marks',
                    dataIndex: 'marks',
                    render: () => (
                        <Tag color={"red"} key={"1"}>
                            10
                        </Tag>
                    )
                },
                {
                    title: 'Action',
                    key: 'action',
                    render: () => (
                        <Space size="middle">
                            <a>View</a>
                            <a>Edit</a>
                            <a>Delete</a>
                        </Space>
                    ),
                },
            ],
        }
    }

    componentDidMount() {
        this.props.fetchUsers();
    }


    render() {
        const {columns} = this.state;
        const {listUser} = this.props;
        return (
            <Table loading={listUser.loading}
                   columns={columns}
                   dataSource={listUser.users}
                   pagination = {{
                       pageSize: 20,
                       total: 60,
                       current: 1,
                       onChange: this.onPageChange


                   }}
            />)
    }
}


function mapStateToProps({users}) {
    console.log(users)
    return {
        listUser: users.listUser
    }

}

export default connect(mapStateToProps, {fetchUsers})(ListUser)
