import React, {useState, useEffect} from 'react';
import {useFormInput} from "../../utils/hooks";

import {Form, Row, Col, Input, Button} from 'antd';
import {connect} from "react-redux";
import {addUser} from "../../actions/users"


function AddUser({addUser}) {
    const [id, setId] = useFormInput("");
    const [name, setName] = useFormInput("");
    const [className, setClassName] = useFormInput("");

    const submit = () => {
        addUser({
            id,
            name,
            className
        })
        console.log(`ID: ${id}, Name: ${name}, Class: ${className}`)
    }
    return (
        <div>
            <h3>Add User</h3>
            <Form>
                <Row style={{margin : "10px"}}>
                    <Col span={4}>ID</Col>
                    <Col span={8}>
                        <Input onChange={setId} />
                    </Col>
                </Row>
                <Row style={{margin : "10px"}}>
                    <Col span={4}>Name</Col>
                    <Col span={8}>
                        <Input onChange={setName}/>
                    </Col>
                </Row>
                <Row style={{margin : "10px"}}>
                    <Col span={4}>Class</Col>
                    <Col span={16}>
                        <Input onChange={setClassName}/>
                    </Col>
                </Row>
                <Button onClick={submit}>Add</Button>
            </Form>
        </div>
    );
}

const mapStateToProps = (state) => {
    return {

    }
}





export default connect(mapStateToProps, {addUser}) (AddUser);
