//     },
// ];


class ListUser extends Component {

    constructor() {
        super()
        this.state = {
            columns: [
                {
                    title: 'ID',
                    dataIndex: 'id',
                    key: 'id',
                    render: text => <a>{text}</a>,
                },
                {
                    title: 'Name',
                    dataIndex: 'name',
                    key: 'name',
                    render: text => <a>{text}</a>,
                },
                {
                    title: 'Class',
                    dataIndex: 'class',
                    key: 'class',
                    render: text => <a>{text}</a>,
                },
                {
                    title: 'Marks',
                    key: 'marks',
                    dataIndex: 'marks',
                    render: () => (
                        <Tag color={"red"} key={"1"}>
                            10
                        </Tag>
                    )
                },
                {
                    title: 'Action',
                    key: 'action',
                    render: () => (
                        <Space size="middle">
                            <a>View</a>
                            <a>Edit</a>
                            <a>Delete</a>
                        </Space>
                    ),
                },
            ],
        }
    }

    componentDidMount() {
        this.props.fetchUsers();
    }

    onPageChange = (page) => {

    }

    onSearch = (key) =>{

    }


    render() {
        const {columns} = this.state;
        const {listUser} = this.props;
        return (
            <>
                <Search placeholder="input search text" onSearch={this.onSearch} enterButton/>
                <Table loading={listUser.loading}
                       columns={columns}
                       dataSource={listUser.users}
                       pagination={{
                           pageSize: 20,
                           total: 60,
                           current: 1,
                           onChange: this.onPageChange


                       }}
                />
            </>
        )
    }
}


function mapStateToProps({users}) {
    console.log(users)
    return {
        listUser: users.listUser
    }

}

export default connect(mapStateToProps, {fetchUsers})(ListUser)
