import React, {Component} from 'react'
import {Table, Tag, Space} from 'antd';
import {connect} from 'react-redux';
import {fetchUsers} from '../../actions/users';

const data = [
    {
        key: '1',
        name: 'John Brown',
        age: 32,
        address: 'New York No. 1 Lake Park',
        tags: ['nice', 'developer'],
    },
    {
        key: '2',
        name: 'Jim Green',
        age: 42,
        address: 'London No. 1 Lake Park',
        tags: ['loser'],
    },
    {
        key: '3',
        name: 'Joe Black',
        age: 32,
        address: 'Sidney No. 1 Lake Park',
        tags: ['cool', 'teacher'],
    },
];

class ListUser extends Component {

    constructor() {
        super()
        this.state = {
            columns: [
                {
                    title: 'ID',
                    dataIndex: 'id',
                    key: 'id',
                    render: text => <a>{text}</a>,
                },
                {
                    title: 'Name',
                    dataIndex: 'name',
                    key: 'name',
                    render: text => <a>{text}</a>,
                },
                {
                    title: 'Class',
                    dataIndex: 'class',
                    key: 'class',
                },
                {
                    title: 'Tags',
                    key: 'tags',
                    dataIndex: 'tags',
                    render: () => (
                        <Tag color={"red"} key={"1"}>
                            test
                        </Tag>
                    )
                },
                {
                    title: 'Action',
                    key: 'action',
                    render: (text, record) => (
                        <Space size="middle">
                            <a>Invite {record.name}</a>
                            <a>Delete</a>
                        </Space>
                    ),
                },
            ],
        }
    }

    componentDidMount() {
        this.props.fetchUsers();
    }


    render() {
        const {columns} = this.state;
        const {listUser} = this.props;
        return (<Table loading={listUser.loading} columns={columns} dataSource={listUser.users}/>
        )
    }
}


function mapStateToProps(state) {
    console.log()
    return {
        listUser: state.users.listUser
    }

}

export default connect(mapStateToProps, {fetchUsers})(ListUser)
