import {auth} from "../apis";
import store from "../store"

export const login = (data) => dispatch => {
    console.log("Starting login action")
    auth.login(data)
        .then((resp) => {
            console.log("resp: " + resp)
            //dispatch to store
            store.dispatch({type: "LOGIN_SUCCESS"})
        })
        .catch((err) => {
            console.log("err: " + err)
            //dispatch to store
            store.dispatch({type: "LOGIN_FAILED"})
        })

}
