const initialState = {
    isLogin : false,
    token: ""
}

export default function authReducer(state = initialState, action = {}) {
    console.log("Action : ",action)

    switch (action.type) {
        case "LOGIN_SUCCESS":
            return {
                ...state,
                isLogin: true
            }
    }
    return state
}
